#include <stdio.h>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

int main() {
    long long n, k, mod = 1e9 + 7;
    scanf("%lld %lld", &n, &k);
    if (n % 2 == 1) {
        long long ans = power(k, n / 2 + 1, mod);
        printf("%lld\n", ans);
    } else {
        long long ans = (power(k, n / 2, mod) * (k + 1)) % mod;
        long long g = power(2, mod - 2, mod);
        ans = (ans * g) % mod;
        printf("%lld\n", ans);
    }
    return 0;
}
