#include <iostream>

using namespace std;

long long power(long long base, long long exp, long long mod) {
  long long res = 1;
  base %= mod;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % mod;
    base = (base * base) % mod;
    exp /= 2;
  }
  return res;
}

int main() {
  long long n, k;
  cin >> n >> k;

  long long mod = 1e9 + 7;

  if (n == 1) {
    cout << k << endl;
    return 0;
  }

  long long half_len = (n + 1) / 2;
  long long ans = power(k, half_len, mod);

  if (n % 2 == 0) {
    ans = (ans * power(k, half_len -1, mod)) % mod;
  }

  cout << ans << endl;
  return 0;
}
