import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long k = sc.nextLong();
        long mod = 1000000007;

        if (n == 1) {
            System.out.println(k);
            return;
        }

        long ans = 1;
        long power = 1;
        long len = (n + 1) / 2;

        for (long i = 0; i < len; i++) {
            ans = (ans * k) % mod;
        }

        if (n % 2 == 0) {
            long res = 1;
            long base = ans;
            long exp = (long) Math.pow(k, (double) (n / 2 - (len -1)));

            while (exp > 0) {
                if (exp % 2 == 1) res = (res * base) % mod;
                base = (base * base) % mod;
                exp /= 2;
            }

            System.out.println(res);

        } else {
            System.out.println(ans);
        }
    }
}
