import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextLong();
        }
        Arrays.sort(a);
        Arrays.sort(b);
        long minSum = 0;
        for (int i = 0; i < n; i++) {
            minSum += Math.abs(a[i] - b[i]);
        }
        long count = 1;
        long mod = 1000000007;
        long[] dp = new long[n + 1];
        dp[0] = 1;
        for (int i = 0; i < n; i++) {
            long[] newDp = new long[n + 1];
            for (int j = 0; j <= i; j++) {
                if (a[i] == b[j]) {
                    newDp[j + 1] = (newDp[j + 1] + dp[j]) % mod;
                }
            }
            for (int j = 0; j <= i; j++) {
                dp[j] = newDp[j];
            }
        }
        long ans = dp[n];
        System.out.println(ans);
    }
}
