import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long nimSum = 0;
        for (long x : a) {
            nimSum ^= x;
        }
        if (nimSum == 0) {
            System.out.println(-1);
            return;
        }
        long minStones = Long.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            long currentNimSum = 0;
            long stonesEaten = 0;
            for (int j = 0; j < n; j++) {
                long val = a[j];
                if ((i >> j) % 2 == 1) {
                    val--;
                    stonesEaten++;
                }
                currentNimSum ^= val;
            }
            if (currentNimSum == 0) {
                minStones = Math.min(minStones, stonesEaten);
            }
        }
        if (minStones == Long.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(minStones);
        }
    }
}
