#include <stdio.h>

int main() {
    double p[6], q[6];
    for (int i = 0; i < 6; ++i) {
        scanf("%lf", &p[i]);
        p[i] /= 100.0;
    }
    for (int i = 0; i < 6; ++i) {
        scanf("%lf", &q[i]);
        q[i] /= 100.0;
    }
    double ans = 0.0;
    for (int i = 1; i <= 6; ++i) {
        double pr = 0.0, qr = 0.0;
        for (int j = 0; j < 6; ++j) {
            if (j + 1 == i) {
                pr += p[j];
                qr += q[j];
            }
        }
        if (pr > qr) {
            ans += pr;
        } else {
            ans += qr;
        }
    }
    ans /= 2.0;
    printf("%.12lf\n", ans);
    return 0;
}
