#include <iostream>
#include <vector>
#include <iomanip>

using namespace std;

int main() {
  vector<double> p(6), q(6);
  for (int i = 0; i < 6; ++i) cin >> p[i];
  for (int i = 0; i < 6; ++i) cin >> q[i];

  for (int i = 0; i < 6; ++i) {
    p[i] /= 100.0;
    q[i] /= 100.0;
  }

  double win_prob = 0.0;
  for (int i = 1; i <= 6; ++i) {
    double prob_red_shows_i = p[i - 1];
    double prob_blue_shows_i = q[i - 1];

    if (prob_red_shows_i == 0 && prob_blue_shows_i ==0) continue;

    double prob_i = prob_red_shows_i + prob_blue_shows_i;
    win_prob += (prob_red_shows_i / prob_i) * prob_red_shows_i + (prob_blue_shows_i / prob_i) * prob_blue_shows_i;

  }

  cout << fixed << setprecision(12) << win_prob / 2.0 << endl;
  return 0;
}
