import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        double[] p = new double[6];
        double[] q = new double[6];
        double sumP = 0;
        double sumQ = 0;
        for (int i = 0; i < 6; i++) {
            p[i] = sc.nextDouble();
            sumP += p[i];
        }
        for (int i = 0; i < 6; i++) {
            q[i] = sc.nextDouble();
            sumQ += q[i];
        }
        for (int i = 0; i < 6; i++) {
            p[i] /= 100;
            q[i] /= 100;
        }

        double ans = 0;
        for (int i = 1; i <= 6; i++) {
            double probRed = p[i - 1];
            double probBlue = q[i - 1];
            double totalProb = probRed + probBlue;
            if (totalProb > 0)
                ans += probRed / totalProb * probRed;

        }
        System.out.printf("%.12f\n", ans);
    }
}
