#include <stdio.h>
#include <math.h>

#define NMAX 15

double dist(double x1, double y1, double x2, double y2) {
    return sqrt(pow(x1 - x2, 2) + pow(y1 - y2, 2));
}

int main() {
    int n;
    scanf("%d", &n);
    double x[NMAX], y[NMAX], a[NMAX];
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf %lf", &x[i], &y[i], &a[i]);
    }
    double left = 0, right = 1e9;
    for (int i = 0; i < 100; i++) {
        double mid = (left + right) / 2;
        double b[NMAX];
        for (int j = 0; j < n; j++) {
            b[j] = a[j];
        }
        bool possible = true;
        for (int j = 0; j < (1 << n); j++) {
            double c[NMAX];
            for (int k = 0; k < n; k++) {
                c[k] = b[k];
            }
            bool cur_possible = true;
            for (int k = 0; k < n; k++) {
                if ((j >> k) & 1) {
                    double min_dist = 1e18;
                    int min_idx = -1;
                    for (int l = 0; l < n; l++) {
                        if (c[l] > mid && dist(x[k], y[k], x[l], y[l]) < min_dist) {
                            min_dist = dist(x[k], y[k], x[l], y[l]);
                            min_idx = l;
                        }
                    }
                    if (min_idx != -1) {
                        double transfer = (c[min_idx] - mid) ;
                        c[k] += transfer - dist(x[k], y[k], x[min_idx], y[min_idx]);
                        c[min_idx] -= transfer;
                    }
                }
            }
            for (int k = 0; k < n; k++) {
                if (c[k] < mid) {
                    cur_possible = false;
                    break;
                }
            }
            if(cur_possible) possible = true; else possible = false;
            if(possible) break;
        }
        if (possible) {
            left = mid;
        } else {
            right = mid;
        }
    }
    printf("%.12lf\n", left);
    return 0;
}
