import java.util.*;
import java.lang.Math;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        double[][] cities = new double[n][3];
        for (int i = 0; i < n; i++) {
            cities[i][0] = sc.nextDouble();
            cities[i][1] = sc.nextDouble();
            cities[i][2] = sc.nextDouble();
        }
        double left = 0, right = 1e9;
        while (right - left > 1e-10) {
            double mid = (left + right) / 2;
            double[][] capacity = new double[n][n];
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    double dist = Math.sqrt(Math.pow(cities[i][0] - cities[j][0], 2) + Math.pow(cities[i][1] - cities[j][1], 2));
                    capacity[i][j] = cities[i][2] - mid - dist;
                    if (capacity[i][j] < 0) capacity[i][j] = 0;
                }
            }
            double[] balance = new double[n];
            for (int i = 0; i < n; i++) {
                balance[i] = cities[i][2] - mid;
            }
            boolean possible = true;
            for (int i = 0; i < n; i++) {
                if (balance[i] < 0) {
                    possible = false;
                    break;
                }
            }
            if (possible) {
                left = mid;
            } else {
                right = mid;
            }
        }
        System.out.printf("%.12f\n", left);
    }
}
