import math

def dist(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def check(x, n, cities):
    graph = [[float('inf')] * n for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            graph[i][j] = graph[j][i] = dist(cities[i][:2], cities[j][:2])

    capacity = [c[2] for c in cities]

    for k in range(n):
        for i in range(n):
            for j in range(n):
                graph[i][j] = min(graph[i][j], graph[i][k] + graph[k][j])

    total_water = sum(capacity)

    needed_water = 0
    for i in range(n):
        needed_water += max(0, x - capacity[i])

    return total_water >= needed_water * 1.0


def solve():
    n = int(input())
    cities = []
    for _ in range(n):
        x, y, a = map(float, input().split())
        cities.append((x, y, a))

    left = 0
    right = 10**9

    for _ in range(100):
        mid = (left + right) / 2
        if check(mid, n, cities):
            left = mid
        else:
            right = mid

    print(left)

