#include <stdio.h>
#include <stdlib.h>

typedef struct {
    long long l, r;
} Interval;

int compare(const void *a, const void *b) {
    Interval *ia = (Interval *)a;
    Interval *ib = (Interval *)b;
    return ia->r - ib->r;
}

int main() {
    int n;
    scanf("%d", &n);
    Interval intervals[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &intervals[i].l, &intervals[i].r);
    }
    qsort(intervals, n, sizeof(Interval), compare);
    long long cost = 0;
    long long current_pos = -intervals[0].r;
    for (int i = 0; i < n; i++) {
        long long move = current_pos - (-intervals[i].l);
        if (move < 0)
            move = 0;
        cost += move;
        current_pos -= intervals[i].l;
        current_pos += intervals[i].r;
        current_pos += move;
        current_pos = -intervals[i].r;


        long long next_pos = -intervals[i].r;
        for(int j = i + 1; j < n; j++){
            if(next_pos < -intervals[j].l){
                long long move_needed = -intervals[j].l - next_pos;
                cost += move_needed;
                next_pos += move_needed;
            }
            next_pos = -intervals[j].r;

        }
    }
    printf("%lld\n", cost);
    return 0;
}
