#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Interval {
    long long l, r;
};

int main() {
    int n;
    cin >> n;

    vector<Interval> intervals(n);
    for (int i = 0; i < n; ++i) {
        cin >> intervals[i].l >> intervals[i].r;
    }

    long long min_cost = -1;

    vector<pair<long long, int>> p(n);
    for (int i = 0; i < n; i++) {
        p[i] = {intervals[i].r + intervals[i].l, i};
    }
    sort(p.begin(), p.end());

    vector<long long> costs;
    for (int i = 0; i < (1 << n); ++i) {
        long long current_cost = 0;
        vector<pair<long long, long long>> current_intervals;
        vector<int> signs(n);

        for (int j = 0; j < n; ++j) {
            if ((i >> j) & 1) {
                signs[j] = 1;
            } else {
                signs[j] = -1;
            }
        }

        vector<tuple<long long, long long, int>> cur;
        for (int j = 0; j < n; j++) {
            cur.emplace_back(intervals[j].l, intervals[j].r, signs[j]);
        }

        vector<pair<long long, long long>> cur_int;
        for (int k = 0; k < n; k++) {
            long long l = get<0>(cur[k]);
            long long r = get<1>(cur[k]);
            int sign = get<2>(cur[k]);
            long long move = 0;
            bool possible = true;
            if (sign == -1) {
              move = l;
            } else {
              move = -r;
            }
            cur_int.emplace_back(l + move, r + move);
            current_cost += abs(move);
        }

        bool overlap = false;
        for (int j = 0; j < n; ++j) {
            for (int k = j + 1; k < n; ++k) {
                if (max(cur_int[j].first, cur_int[k].first) < min(cur_int[j].second, cur_int[k].second)) {
                    overlap = true;
                    break;
                }
            }
            if (overlap) break;
        }

        if (!overlap) {
            if (min_cost == -1 || current_cost < min_cost) {
                min_cost = current_cost;
            }
        }
    }

    cout << min_cost << endl;

    return 0;
}
