import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] intervals = new long[n][2];
        for (int i = 0; i < n; i++) {
            intervals[i][0] = -sc.nextInt();
            intervals[i][1] = sc.nextInt();
        }
        Arrays.sort(intervals, (a, b) -> Long.compare(a[1], b[1]));
        long cost = 0;
        long[] ends = new long[n];
        ends[0] = intervals[0][1];
        for (int i = 1; i < n; i++) {
            long requiredEnd = intervals[i][1];
            long currentEnd = ends[i - 1];
            if (intervals[i][0] <= currentEnd) {
                long diff = currentEnd - intervals[i][0] + 1;
                cost += diff;
                ends[i] = currentEnd + diff + intervals[i][1] - intervals[i][0];
            } else {
                ends[i] = intervals[i][1];
            }
        }
        System.out.println(cost);
    }
}

