#include <stdio.h>

#define MOD 1000000007

int main() {
    int N;
    scanf("%d", &N);
    int C[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            scanf("%d", &C[i][j]);
        }
    }
    long long ans = 1;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (C[i][j] == 0) {
                ans = (ans * 1) % MOD;
            } else {
                ans = (ans * 3) % MOD;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}

