#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<vector<long long>> c(n, vector<long long>(n));
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      cin >> c[i][j];
    }
  }

  long long count = 0;
  long long mod = 1e9 + 7;

  for (long long i = 0; i < (1LL << (n * n)); ++i) {
    vector<vector<long long>> a(n, vector<long long>(n));
    long long temp = i;
    for (int r = 0; r < n; ++r) {
      for (int s = 0; s < n; ++s) {
        a[r][s] = temp % 2;
        temp /= 2;
      }
    }

    for (long long j = 0; j < (1LL << (n * n)); ++j) {
      vector<vector<long long>> b(n, vector<long long>(n));
      temp = j;
      for (int r = 0; r < n; ++r) {
        for (int s = 0; s < n; ++s) {
          b[r][s] = temp % 2;
          temp /= 2;
        }
      }

      vector<vector<long long>> prod(n, vector<long long>(n, 0));
      for (int r = 0; r < n; ++r) {
        for (int s = 0; s < n; ++s) {
          for (int k = 0; k < n; ++k) {
            prod[r][s] = (prod[r][s] + a[r][k] * b[k][s]) % 2;
          }
        }
      }

      bool match = true;
      for (int r = 0; r < n; ++r) {
        for (int s = 0; s < n; ++s) {
          if (prod[r][s] != c[r][s]) {
            match = false;
            break;
          }
        }
        if (!match) break;
      }
      if (match) count++;
    }
  }

  cout << count % mod << endl;
  return 0;
}
