MOD = 10**9 + 7

def solve():
    n = int(input())
    c = []
    for _ in range(n):
        c.append(list(map(int, input().split())))

    ans = 1
    for i in range(n):
        for j in range(n):
            if c[i][j] == 0:
                ans = (ans * pow(2, n, MOD)) % MOD
            else:
                count_zero = 0
                for k in range(n):
                    if c[i][k] == 0:
                        count_zero +=1
                if count_zero==n:
                    continue
                ans = (ans * pow(2, n-1,MOD)) % MOD


    print(ans)

solve()
