#include <iostream>
#include <vector>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    if (n % 2 != 0) {
        cout << -1 << endl;
        return 0;
    }

    vector<pair<long long, long long>> points(n);
    points[0] = {0, 0};
    long long cur_x = 0, cur_y = 0;
    int dx = 1, dy = 0;

    for (int i = 0; i < n; ++i) {
        if (a[i] == 90) {
            if (dx == 1) {
                cur_x +=1;
            } else if (dx == -1){
                cur_x -=1;
            } else if (dy == 1){
                cur_y +=1;
            } else {
                cur_y -=1;
            }


            points[i+1] = {cur_x, cur_y};

            if(dx == 1) {
                dy = 1;
                dx = 0;
            } else if(dy == 1){
                dx = -1;
                dy = 0;
            } else if(dx == -1){
                dy = -1;
                dx = 0;
            } else {
                dx = 1;
                dy = 0;
            }
        } else {
            if (dx == 1) {
                cur_x -=1;
            } else if (dx == -1){
                cur_x +=1;
            } else if (dy == 1){
                cur_y -=1;
            } else {
                cur_y +=1;
            }
            points[i+1] = {cur_x, cur_y};
            if(dx == 1) {
                dy = -1;
                dx = 0;
            } else if(dy == -1){
                dx = -1;
                dy = 0;
            } else if(dx == -1){
                dy = 1;
                dx = 0;
            } else {
                dx = 1;
                dy = 0;
            }
        }

    }

    for (int i = 0; i < n; ++i) {
        cout << points[i].first << " " << points[i].second << endl;
    }

    return 0;
}
