#include <stdio.h>

#define MOD 1000000007

long long dp[5001][5001][5001];

int main() {
    int n, a, b, c;
    scanf("%d %d %d %d", &n, &a, &b, &c);

    dp[0][0][0] = 1;

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= n; k++) {
                if (i + j + k > n || dp[i][j][k] == 0) continue;

                if (i + 1 <= n) dp[i + 1][j][k] = (dp[i + 1][j][k] + dp[i][j][k]) % MOD;
                if (j + 1 <= n) dp[i][j + 1][k] = (dp[i][j + 1][k] + dp[i][j][k]) % MOD;
                if (k + 1 <= n) dp[i][j][k + 1] = (dp[i][j][k + 1] + dp[i][j][k]) % MOD;

                if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][k] = (dp[i + 1][j + 1][k] + dp[i][j][k]) % MOD;
                if (i + 1 <= n && k + 1 <= n) dp[i + 1][j][k + 1] = (dp[i + 1][j][k + 1] + dp[i][j][k]) % MOD;
                if (j + 1 <= n && k + 1 <= n) dp[i][j + 1][k + 1] = (dp[i][j + 1][k + 1] + dp[i][j][k]) % MOD;

                if (i + 1 <= n && j + 1 <= n && k + 1 <= n) dp[i + 1][j + 1][k + 1] = (dp[i + 1][j + 1][k + 1] + dp[i][j][k]) % MOD;

            }
        }
    }

    printf("%lld\n", dp[a][b][c]);

    return 0;
}
