#include <stdio.h>

#include <limits.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    long long A[N], B[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &A[i], &B[i]);
    }
    long long dp[1 << N];
    for (int i = 0; i < (1 << N); i++) {
        dp[i] = 0;
    }
    for (int i = 0; i < (1 << N); i++) {
        long long orVal = 0;
        long long sum = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                orVal |= A[j];
                sum += B[j];
            }
        }
        if (orVal <= K) {
            dp[i] = sum;
        }
    }
    long long maxSum = 0;
    for (int i = 0; i < (1 << N); i++) {
        maxSum = max(maxSum, dp[i]);
    }
    printf("%lld\n", maxSum);
    return 0;
}
