import java.util.*;
import java.math.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int c = sc.nextInt();

        long[][] dp = new long[n + 1][n + 1];
        dp[0][0] = 1;

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (dp[i][j] == 0) continue;

                if (i + 1 <= n && j + 1 <= n) {
                    dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % MOD;
                }
                if (i + 2 <= n && j + 1 <= n) {
                    dp[i + 2][j + 1] = (dp[i + 2][j + 1] + dp[i][j]) % MOD;
                }
                if (i + 3 <= n && j + 1 <= n) {
                    dp[i + 3][j + 1] = (dp[i + 3][j + 1] + dp[i][j]) % MOD;
                }

                if (i + 1 <= n && j <= n) {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD;
                }
                if (i + 2 <= n && j <= n) {
                    dp[i + 2][j] = (dp[i + 2][j] + dp[i][j]) % MOD;
                }
                if (i + 3 <= n && j <= n) {
                    dp[i + 3][j] = (dp[i + 3][j] + dp[i][j]) % MOD;
                }
            }
        }

        long ans = 0;
        for (int i = 0; i <= n; i++) {
            long cur = dp[a + b + c][i];
            if (i + a + b + c == n) {
                ans = (ans + cur) % MOD;
            }
        }
        System.out.println(ans);
    }
}
