#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int cmp(const void *a, const void *b) {
    return *(long long *)a - *(long long *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    long long a[n], b[n];
    for (int i = 0; i < n; i++)
        scanf("%lld", &a[i]);
    for (int i = 0; i < n; i++)
        scanf("%lld", &b[i]);
    qsort(a, n, sizeof(long long), cmp);
    qsort(b, n, sizeof(long long), cmp);
    long long dp[n + 1][n + 1];
    for (int i = 0; i <= n; i++)
        for (int j = 0; j <= n; j++)
            dp[i][j] = 0;
    dp[0][0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            if (dp[i][j] == 0)
                continue;
            if (a[i] < b[i - j]) {
                if (a[i] < b[n - 1 - j]) {
                    dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % MOD;
                }
            } else {
                dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD;
            }
        }
    }
    printf("%lld\n", dp[n][n / 2]);
    return 0;
}
