#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
  }

  sort(a.begin(), a.end());
  sort(b.begin(), b.end());

  long long min_sum = -1;
  long long count = 0;

  vector<int> p(n);
  for (int i = 0; i < n; ++i) {
    p[i] = i;
  }

  do {
    long long cur_sum = 0;
    for (int i = 0; i < n; ++i) {
      cur_sum += abs(a[i] - b[p[i]]);
    }

    if (min_sum == -1 || cur_sum < min_sum) {
      min_sum = cur_sum;
      count = 1;
    } else if (cur_sum == min_sum) {
      count++;
    }
  } while (next_permutation(p.begin(), p.end()));

  cout << count << endl;
  return 0;
}
