import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextLong();
        }
        Arrays.sort(a);
        Arrays.sort(b);
        long minSum = 0;
        for (int i = 0; i < n; i++) {
            minSum += Math.abs(a[i] - b[i]);
        }
        long count = 0;
        long mod = 1000000007;
        long[] dp = new long[1 << n];
        dp[0] = 1;
        for (int i = 0; i < (1 << n); i++) {
            if (dp[i] == 0) continue;
            for (int j = 0; j < n; j++) {
                if ((i >> j) == 0) {
                    long curSum = 0;
                    int k = i;
                    for (int l = 0; l < n; l++) {
                        if ((k >> l) == 0) {
                            curSum += Math.abs(a[l] - b[j]);
                        }
                    }
                    if (curSum == minSum) {
                        dp[i | (1 << j)] = (dp[i | (1 << j)] + dp[i]) % mod;
                    }
                }
            }
        }
        System.out.println(dp[(1 << n) - 1]);
    }
}
