MOD = 10**9 + 7

def solve():
    n = int(input())
    a = [int(input()) for _ in range(n)]
    b = [int(input()) for _ in range(n)]

    a.sort()
    b.sort()

    dp = {}

    def count_ways(i, j):
        if i == n:
            return 1

        if (i, j) in dp:
            return dp[(i, j)]

        count = 0
        for k in range(j, n):
            count = (count + count_ways(i + 1, k + 1)) % MOD

        dp[(i, j)] = count
        return count

    min_len = float('inf')
    ways = 0


    def find_min_ways(idx_a, idx_b, current_len, used_b):
        nonlocal min_len, ways
        if idx_a == n:
            if current_len < min_len:
                min_len = current_len
                ways = 1
            elif current_len == min_len:
                ways +=1
            return

        for i in range(n):
            if i not in used_b:
                find_min_ways(idx_a + 1, idx_b + 1, current_len + abs(a[idx_a] - b[i]), used_b | {i})

    find_min_ways(0,0,0,set())
    print(ways % MOD)

solve()
```
