#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    long long nim_sum = 0;
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
        nim_sum ^= a[i];
    }
    long long min_stones = -1;
    for (int i = 0; i < (1 << n); i++) {
        long long current_nim_sum = 0;
        long long stones_eaten = 0;
        for (int j = 0; j < n; j++) {
            long long pile_size = a[j];
            if ((i >> j) & 1) {
                pile_size--;
                stones_eaten++;
            }
            current_nim_sum ^= pile_size;
        }
        if (current_nim_sum == 0) {
            if (min_stones == -1 || stones_eaten < min_stones) {
                min_stones = stones_eaten;
            }
        }
    }
    printf("%lld\n", min_stones);
    return 0;
}
