#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_eaten = -1;
  for (int i = 0; i < (1 << n); ++i) {
    long long eaten = 0;
    vector<long long> b = a;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        eaten++;
        b[j]--;
      }
    }

    long long nim_sum = 0;
    for (long long x : b) {
      nim_sum ^= x;
    }

    if (nim_sum == 0) {
      if (min_eaten == -1 || eaten < min_eaten) {
        min_eaten = eaten;
      }
    }
  }

  cout << min_eaten << endl;
  return 0;
}
