n = int(input())
a = []
for _ in range(n):
    a.append(int(input()))

nim_sum = 0
for x in a:
    nim_sum ^= x

min_stones = float('inf')
for i in range(1 << n):
    stones_eaten = 0
    current_nim_sum = 0
    temp_a = []
    for j in range(n):
        if (i >> j) & 1:
            stones_eaten += 1
            temp_a.append(a[j] - 1)
        else:
            temp_a.append(a[j])

    for x in temp_a:
        current_nim_sum ^= x

    if current_nim_sum == 0:
        min_stones = min(min_stones, stones_eaten)

if min_stones == float('inf'):
    print(-1)
else:
    print(min_stones)

