#include <stdio.h>

int main() {
    double p[6], q[6], prob_red = 0.0, prob_blue = 0.0;
    for (int i = 0; i < 6; i++) {
        scanf("%lf", &p[i]);
        p[i] /= 100.0;
    }
    for (int i = 0; i < 6; i++) {
        scanf("%lf", &q[i]);
        q[i] /= 100.0;
    }
    double win_prob = 0.0;
    for (int i = 0; i < 6; i++) {
        prob_red += p[i];
        prob_blue += q[i];
    }
    double red_win_prob = 0.0;
    double blue_win_prob = 0.0;
    for(int i=0; i<6; ++i){
        double red_prob = p[i] / prob_red;
        double blue_prob = q[i] / prob_blue;
        if(red_prob > blue_prob){
            red_win_prob += p[i];
        } else if (blue_prob > red_prob){
            blue_win_prob += q[i];
        } else {
            red_win_prob += p[i] / 2.0;
            blue_win_prob += q[i] / 2.0;
        }
    }


    printf("%.12lf\n", (red_win_prob + blue_win_prob) /2);

    return 0;
}
