import java.util.Scanner;

public class DiceGame {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double[] p = new double[6];
        double[] q = new double[6];
        for (int i = 0; i < 6; i++) {
            p[i] = scanner.nextDouble() / 100.0;
        }
        for (int i = 0; i < 6; i++) {
            q[i] = scanner.nextDouble() / 100.0;
        }
        scanner.close();

        double winProbability = 0;
        for (int i = 0; i < 6; i++) {
            double probRed = p[i];
            double probBlue = q[i];
            if (probRed > probBlue) {
                winProbability += probRed;
            } else if (probBlue > probRed) {
                winProbability += probBlue;
            } else {
                winProbability += probRed;
            }
        }

        winProbability /= 2;

        System.out.printf("%.12f\n", winProbability);
    }
}
