#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 15

double dist(double x1, double y1, double x2, double y2) {
    return sqrt(pow(x1 - x2, 2) + pow(y1 - y2, 2));
}

int main() {
    int n;
    scanf("%d", &n);
    double x[MAXN], y[MAXN], a[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf %lf", &x[i], &y[i], &a[i]);
    }
    double l = 0, r = 1e9;
    while (r - l > 1e-10) {
        double mid = (l + r) / 2;
        double b[MAXN];
        for (int i = 0; i < n; i++) {
            b[i] = a[i];
        }
        bool flag = true;
        for (int i = 0; i < (1 << n); i++) {
            double c[MAXN];
            for (int j = 0; j < n; j++) {
                c[j] = b[j];
            }
            bool ok = true;
            for (int j = 0; j < n; j++) {
                if ((i >> j) & 1) {
                    double mn = 1e18;
                    int idx = -1;
                    for (int k = 0; k < n; k++) {
                        if (c[k] < mn) {
                            mn = c[k];
                            idx = k;
                        }
                    }
                    if (idx == -1 || c[idx] < mid) {
                        ok = false;
                        break;
                    }
                    double transfer = (c[idx] - mid);
                    c[idx] -= transfer;
                    for (int k = 0; k < n; k++) {
                        if (!((i >> k) & 1)) {
                            double d = dist(x[idx], y[idx], x[k], y[k]);
                            c[k] += fmax(0, transfer - d);
                        }
                    }
                }
            }
            if (ok) {
                flag = false;
                break;
            }
        }
        if (flag) {
            r = mid;
        } else {
            l = mid;
        }
    }
    printf("%.12lf\n", l);
    return 0;
}
