import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextLong();
        long[][] items = new long[n][2];
        for (int i = 0; i < n; i++) {
            items[i][0] = sc.nextLong();
            items[i][1] = sc.nextLong();
        }
        long maxUtility = 0;
        for (int i = 0; i < (1 << n); i++) {
            long currentOr = 0;
            long currentUtility = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    currentOr |= items[j][0];
                    currentUtility += items[j][1];
                }
            }
            if (currentOr <= k) {
                maxUtility = Math.max(maxUtility, currentUtility);
            }
        }
        System.out.println(maxUtility);
        sc.close();
    }
}
