#include <stdio.h>
#include <stdlib.h>

typedef struct {
    long long l, r;
} Interval;

int compare(const void *a, const void *b) {
    Interval *ia = (Interval *)a;
    Interval *ib = (Interval *)b;
    return ia->r - ib->r;
}

int main() {
    int n;
    scanf("%d", &n);
    Interval intervals[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &intervals[i].l, &intervals[i].r);
    }
    qsort(intervals, n, sizeof(Interval), compare);
    long long cost = 0;
    long long rightmost = 0;
    for (int i = 0; i < n; i++) {
        if (intervals[i].l - rightmost > 0) {
            cost += intervals[i].l - rightmost;
            rightmost = intervals[i].r;

        } else if (intervals[i].l >= -rightmost) {
            rightmost = intervals[i].r;
        } else {
            cost += rightmost + intervals[i].l + 1;
            rightmost = rightmost + intervals[i].l + 1 + intervals[i].r;

        }
    }
    printf("%lld\n", cost);
    return 0;
}
