import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] intervals = new long[n][2];
        for (int i = 0; i < n; i++) {
            intervals[i][0] = -sc.nextLong();
            intervals[i][1] = sc.nextLong();
        }
        Arrays.sort(intervals, (a, b) -> Long.compare(a[1], b[1]));
        long cost = 0;
        long[] pos = new long[n];
        pos[0] = intervals[0][1];
        for (int i = 1; i < n; i++) {
            long maxReach = pos[i - 1];
            long required = intervals[i][0] + intervals[i][1] +1 ;
            if (required > maxReach) {
                pos[i] = required;
            } else {
                pos[i] = maxReach + 1;

            }
            cost += Math.abs(intervals[i][1] - (pos[i] - intervals[i][0]));

        }
        System.out.println(cost);
    }
}
