    char s[5001];
    sprintf(s, "F");
    long long num = 1;
    for (long long i = 0; i < 6; i++) {
      char c = "ESTIVAL"[i];
      long long count = 1;
      while (num * (count + 1) <= k) {
          count++;
      }
        for(long long j=0; j<count; ++j){
            strcat(s, &c);
        }
      num *= (count + 1);
    }
    strcat(s, "L");
    printf("%s\n", s);
    return 0;
}
