#include <stdio.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int c[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%d", &c[i][j]);
        }
    }
    long long ans = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (c[i][j] == 0) {
                ans = (ans * 1) % MOD;
            } else {
                ans = (ans * 3) % MOD;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}

