MOD = 10**9 + 7

def solve():
    n = int(input())
    c = []
    for _ in range(n):
        c.append(list(map(int, input().split())))

    ans = 1
    for i in range(n):
        for j in range(n):
            if c[i][j] == 0:
                continue
            count = 0
            for k in range(n):
                if c[i][k] == 1 and c[k][j] ==1:
                    count +=1
            if count ==0:
                ans = (ans * (2**n)) % MOD
    print(ans)

solve()
