#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    int count90 = 0;
    int count270 = 0;
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        if (a[i] == 90)
            count90++;
        else
            count270++;
    }
    if (count90 != count270) {
        printf("-1\n");
        return 0;
    }
    int x = 0, y = 0;
    int dx = 1, dy = 0;
    printf("0 0\n");
    for (int i = 0; i < n - 1; i++) {
        if (a[i] == 90) {
            x += dx;
            y += dy;
            int temp = dx;
            dx = -dy;
            dy = temp;

        } else {
            x += dx;
            y += dy;
            int temp = dx;
            dx = dy;
            dy = -temp;
        }
        printf("%d %d\n", x, y);
    }
    return 0;
}
