#include <iostream>
#include <vector>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    if (n % 2 != 0) {
        cout << -1 << endl;
        return 0;
    }

    vector<pair<long long, long long>> points(n);
    points[0] = {0, 0};
    long long x = 0, y = 0;
    long long dx = 1, dy = 0;

    for (int i = 1; i < n; ++i) {
        if (a[i - 1] == 90) {
            x += dx;
            y += dy;
        } else {
            x -= dx;
            y -= dy;
        }
        points[i] = {x, y};
        if (a[i] == 90) {
            long long temp = dx;
            dx = -dy;
            dy = temp;
        } else {
            long long temp = dx;
            dx = dy;
            dy = -temp;
        }

    }

    if(points[n-1].first !=0 || points[n-1].second != 0){
        cout << -1 << endl;
        return 0;
    }

    for (int i = 0; i < n; ++i) {
        cout << points[i].first << " " << points[i].second << endl;
    }

    return 0;
}
