import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int x = 0, y = 0;
        int dx = 1, dy = 0;
        int[][] ans = new int[n][2];
        ans[0][0] = x;
        ans[0][1] = y;
        for (int i = 1; i < n; i++) {
            if (a[i - 1] == 90) {
                x += dx;
                y += dy;
            } else {
                x -= dx;
                y -= dy;
            }
            if (a[i] == 90) {
                int temp = dx;
                dx = -dy;
                dy = temp;
            } else {
                int temp = dx;
                dx = dy;
                dy = -temp;
            }
            ans[i][0] = x;
            ans[i][1] = y;
        }
        if (ans[n - 1][0] != ans[0][0] || ans[n - 1][1] != ans[0][1]) {
            System.out.println("-1");
            return;
        }
        for (int i = 0; i < n; i++) {
            System.out.println(ans[i][0] + " " + ans[i][1]);
        }
    }
}
