#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, a, b, c;
  cin >> n >> a >> b >> c;

  long long mod = 1e9 + 7;

  vector<vector<vector<long long>>> dp(n + 1, vector<vector<long long>>(n + 1, vector<long long>(n + 1, 0)));
  dp[0][0][0] = 1;

  for (long long i = 0; i <= n; ++i) {
    for (long long j = 0; j <= n; ++j) {
      for (long long k = 0; k <= n; ++k) {
        if (dp[i][j][k] == 0) continue;

        if (i + 1 <= n) {
          dp[i + 1][j][k] = (dp[i + 1][j][k] + dp[i][j][k]) % mod;
        }
        if (j + 1 <= n) {
          dp[i][j + 1][k] = (dp[i][j + 1][k] + dp[i][j][k]) % mod;
        }
        if (k + 1 <= n) {
          dp[i][j][k + 1] = (dp[i][j][k + 1] + dp[i][j][k]) % mod;
        }

        if (i + 1 <= n && j + 1 <= n) {
          dp[i + 1][j + 1][k] = (dp[i + 1][j + 1][k] + dp[i][j][k]) % mod;
        }
        if (i + 1 <= n && k + 1 <= n) {
          dp[i + 1][j][k + 1] = (dp[i + 1][j][k + 1] + dp[i][j][k]) % mod;
        }
        if (j + 1 <= n && k + 1 <= n) {
          dp[i][j + 1][k + 1] = (dp[i][j + 1][k + 1] + dp[i][j][k]) % mod;
        }
        if (i + 1 <= n && j + 1 <= n && k + 1 <= n) {
          dp[i + 1][j + 1][k + 1] = (dp[i + 1][j + 1][k + 1] + dp[i][j][k]) % mod;
        }
      }
    }
  }

  cout << dp[a][b][c] << endl;
  return 0;
}
