MOD = 10**9 + 7

def solve():
    n, a, b, c = map(int, input().split())

    dp = {}

    def count_ways(rem_n, rem_a, rem_b, rem_c, used):
        if rem_n == 0:
            return 1

        state = (rem_n, rem_a, rem_b, rem_c, used)
        if state in dp:
            return dp[state]

        count = 0

        if rem_a > 0 and not (used & (1 << (rem_n))):
            count = (count + count_ways(rem_n - 1, rem_a - 1, rem_b, rem_c, used | (1 << rem_n) | (1 << (rem_n + 1)))) % MOD

        if rem_b > 0 and not (used & (1 << rem_n)) and not (used & (1 << (rem_n+1))):
            count = (count + count_ways(rem_n - 1, rem_a, rem_b - 1, rem_c, used | (1 << rem_n) | (1 << (rem_n + 2)))) % MOD

        if rem_c > 0 and not (used & (1 << rem_n)) and not (used & (1 << (rem_n+1))) and not (used & (1 << (rem_n+2))):
            count = (count + count_ways(rem_n - 1, rem_a, rem_b, rem_c - 1, used | (1 << rem_n) | (1 << (rem_n + 3)))) % MOD

        dp[state] = count
        return count

    print(count_ways(n, a, b, c, 0))

solve()
