#include <stdio.h>
#include <string.h>
#include <stdlib.h>

char mirror(char c) {
    if (c == 'b') return 'd';
    if (c == 'd') return 'b';
    if (c == 'p') return 'q';
    return 'p';
}

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    char reversed[100001];
    for (int i = 0; i < n; i++) {
        reversed[i] = mirror(s[n - 1 - i]);
    }
    reversed[n] = '\0';
    if (strcmp(s, reversed) == 0) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
