#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

char mirror(char c) {
  if (c == 'b') return 'd';
  if (c == 'd') return 'b';
  if (c == 'p') return 'q';
  return 'p';
}

int main() {
  string s;
  cin >> s;

  string reversed_s = s;
  reverse(reversed_s.begin(), reversed_s.end());

  string mirrored_s = "";
  for (char c : reversed_s) {
    mirrored_s += mirror(c);
  }

  if (s == mirrored_s) {
    cout << "Yes" << endl;
  } else {
    cout << "No" << endl;
  }
  return 0;
}
