import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        String reversed = new StringBuilder(s).reverse().toString();
        String mirrored = "";
        for (char c : reversed.toCharArray()) {
            if (c == 'b') mirrored += 'd';
            else if (c == 'd') mirrored += 'b';
            else if (c == 'p') mirrored += 'q';
            else mirrored += 'p';
        }
        if (s.equals(mirrored)) System.out.println("Yes");
        else System.out.println("No");
    }
}
