#include <stdio.h>

long long solve(long long arr[], long long n) {
    if (n == 1) return arr[0];
    long long newArr[(1LL << (n - 1))];
    for (long long i = 0; i < (1LL << n); i += 2) {
        if (arr[i] > arr[i + 1]) {
            newArr[i / 2] = arr[i] - arr[i + 1];
        } else if (arr[i] < arr[i + 1]) {
            newArr[i / 2] = arr[i + 1] - arr[i];
        } else {
            newArr[i / 2] = arr[i];
        }
    }
    return solve(newArr, n - 1);
}

int main() {
    long long n;
    scanf("%lld", &n);
    long long arr[(1LL << n)];
    for (long long i = 0; i < (1LL << n); i++) {
        scanf("%lld", &arr[i]);
    }
    printf("%lld\n", solve(arr, n));
    return 0;
}
