#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  long long size = 1LL << n;
  vector<long long> a(size);
  for (long long i = 0; i < size; ++i) {
    cin >> a[i];
  }

  vector<long long> b = a;
  while (b.size() > 1) {
    vector<long long> next_b;
    for (long long i = 0; i < b.size(); i += 2) {
      if (b[i] > b[i + 1]) {
        next_b.push_back(b[i] - b[i + 1]);
      } else if (b[i] < b[i + 1]) {
        next_b.push_back(b[i + 1] - b[i]);
      } else {
        next_b.push_back(b[i]);
      }
    }
    b = next_b;
  }

  cout << b[0] << endl;
  return 0;
}
