import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int size = 1 << n;
        long[] a = new long[size];
        for (int i = 0; i < size; i++) {
            a[i] = sc.nextLong();
        }
        while (size > 1) {
            long[] b = new long[size / 2];
            for (int i = 0; i < size / 2; i++) {
                long x = a[2 * i];
                long y = a[2 * i + 1];
                if (x > y) {
                    b[i] = x - y;
                } else if (x == y) {
                    b[i] = x;
                } else {
                    b[i] = y - x;
                }
            }
            a = b;
            size /= 2;
        }
        System.out.println(a[0]);
    }
}
