def solve():
    n = int(input())
    a = [int(input()) for _ in range(2**n)]

    while len(a) > 1:
        b = []
        for i in range(0, len(a), 2):
            x = a[i]
            y = a[i+1]
            if x > y:
                b.append(x - y)
            elif x == y:
                b.append(x)
            else:
                b.append(y - x)
        a = b
    print(a[0])

solve()
