#include <stdio.h>
#include <stdlib.h>

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    long long A, B, C, D;
    scanf("%lld %lld %lld %lld", &A, &B, &C, &D);
    long long dx = abs(C - A);
    long long dy = abs(D - B);
    printf("%lld\n", dx + dy - gcd(dx, dy));
    return 0;
}
